$(document).ready(function() {
	"use strict";
	new Quill(".snow-container .compose-editor",
		{
			modules:
				{ toolbar: ".compose-quill-toolbar" },
			placeholder: "Reply Here", theme: "snow"
		}),
		new Quill(".snow-container .forward-email",
			{
				modules:
					{ toolbar: ".forward-email-toolbar" },
				placeholder: "Your Message", theme: "snow"
			});
		0 < $("html[data-textdirection='rtl']").length && $("#email-sidenav").sidenav({
			edge: "right",
			onOpenStart: function() { $("#sidebar-list").addClass("sidebar-show") },
			onCloseEnd: function() { $("#sidebar-list").removeClass("sidebar-show") }
		})

}),

	$(window).on("resize",
		function() {
			899 < $(window).width() && $("#email-sidenav").removeClass("sidenav"),
			$(window).width() < 900 && $("#email-sidenav").addClass("sidenav")
		});